&lt;li&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly9vbmxpbmVqdWRnZS5vcmcvaW5kZXgucGhwP29wdGlvbj1jb21fb25saW5lanVkZ2UmYW1wO0l0ZW1pZD04JmFtcDtjYXRlZ29yeT0xMiZhbXA7cGFnZT1zaG93X3Byb2JsZW0mYW1wO3Byb2JsZW09OTYw&#34;&gt;Online Judge&lt;/span&gt;&lt;/li&gt;
&lt;li&gt;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cHM6Ly93d3cudWRlYnVnLmNvbS9VVmEvMTAwMTk=&#34;&gt;uDebug&lt;/span&gt;&lt;/li&gt;
