&lt;h2 id=&#34;add-exec-maven-plugin&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#add-exec-maven-plugin&#34;&gt;#&lt;/a&gt; Add exec-maven-plugin&lt;/h2&gt;
&lt;pre&gt;&lt;code class=&#34;language-xml!&#34;&gt;&amp;lt;build&amp;gt;
    &amp;lt;pluginManagement&amp;gt;
      &amp;lt;plugins&amp;gt;
        &amp;lt;!-- 其他 plugins ... --&amp;gt;

        &amp;lt;!--===== 加入這段 =====--&amp;gt;
        &amp;lt;plugin&amp;gt;
          &amp;lt;groupId&amp;gt;org.codehaus.mojo&amp;lt;/groupId&amp;gt;
          &amp;lt;artifactId&amp;gt;exec-maven-plugin&amp;lt;/artifactId&amp;gt;
          &amp;lt;version&amp;gt;1.2.1&amp;lt;/version&amp;gt;
          &amp;lt;executions&amp;gt;
            &amp;lt;execution&amp;gt;
              &amp;lt;goals&amp;gt;
                &amp;lt;goal&amp;gt;java&amp;lt;/goal&amp;gt;
              &amp;lt;/goals&amp;gt;
            &amp;lt;/execution&amp;gt;
          &amp;lt;/executions&amp;gt;
          &amp;lt;configuration&amp;gt;
            &amp;lt;mainClass&amp;gt;com.mycompany.app.App&amp;lt;/mainClass&amp;gt;
          &amp;lt;/configuration&amp;gt;
        &amp;lt;/plugin&amp;gt;
        &amp;lt;!--===== 加入這段 =====--&amp;gt;

        &amp;lt;!-- 其他 plugins ... --&amp;gt;
      &amp;lt;/plugins&amp;gt;
    &amp;lt;/pluginManagement&amp;gt;
  &amp;lt;/build&amp;gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;pre&gt;&lt;code class=&#34;language-xml!&#34;&gt;&amp;lt;project xmlns=&amp;quot;http://maven.apache.org/POM/4.0.0&amp;quot; xmlns:xsi=&amp;quot;http://www.w3.org/2001/XMLSchema-instance&amp;quot;
  xsi:schemaLocation=&amp;quot;http://maven.apache.org/POM/4.0.0 http://maven.apache.org/maven-v4_0_0.xsd&amp;quot;&amp;gt;
  &amp;lt;modelVersion&amp;gt;4.0.0&amp;lt;/modelVersion&amp;gt;
  &amp;lt;groupId&amp;gt;jar.app&amp;lt;/groupId&amp;gt;
